$(function() {
    "use strict";

    // Initialisation de Feather Icons
    feather.replace();

    // Masquer le preloader
    $(".preloader").fadeOut();

    // Gestion du basculement de la barre de navigation
    $(".nav-toggler").on("click", function() {
        $("#main-wrapper").toggleClass("show-sidebar");
        $(".nav-toggler i").toggleClass("ti-menu");
    });

    // Gestion du panneau de service
    $(".service-panel-toggle").on("click", function() {
        $(".customizer").toggleClass("show-service-panel");
    });

    $(".page-wrapper").on("click", function() {
        $(".customizer").removeClass("show-service-panel");
    });


    // Activation de PerfectScrollbar
    $(".message-center, .customizer-body, .scrollable, .scroll-sidebar").perfectScrollbar({
        wheelPropagation: true
    });

    // Déclencher un redimensionnement
    $("body, .page-wrapper").trigger("resize");

    // Afficher la page avec un délai
    $(".page-wrapper").delay(20).show();

    // Gestion des tâches de la liste
    $(".list-task li label").click(function() {
        $(this).toggleClass("task-done");
    });

    // Afficher/masquer la partie gauche
    $(".show-left-part").on("click", function() {
        $(".left-part").toggleClass("show-panel");
        $(".show-left-part").toggleClass("ti-menu");
    });

    // Gestion de l'input de fichier personnalisé
    $(".custom-file-input").on("change", function() {
        var fileName = $(this).val();
        $(this).next(".custom-file-label").html(fileName);
    });
});
